mapper.update:
UPDATE schedule_tb
SET startDate = #{starDate}, endDate = #{endDate}, title = #{title}, place = #{place}, contents = #{contents}
WHERE id = #{scheduleId}

mapper.select:
SELECT id AS scheduleId, startDate, endDate, title
FROM schedule_tb
WHERE teamCode = #{teamCode}

SELETE id AS scheduleId, startDate, endDate, title, place, contents
FROM schedule_tb
WHERE startDate = #{startDate}

SELECT id AS scheduleId, startDate, endDate, title, place, contents
FROM schedule_tb
WHERE id = #{id}
