mapper.insert:
		insert into task_tb(

		id
		,TITLE
		,CONTENTS
		,DEADLINE
		,FLAG
		,POINT)

		values(
		task_tb_id_seq.nextval
		,#{title}
		,#{contents, jdbcType=DATE}
		,#{deadline , jdbcType=INTEGER}
		,#{flag , jdbcType=INTEGER}
		,#{point , jdbcType=INTEGER}
		)

mapper.update:
UPDATE TASK_TB
SET 
	ID						= #{taskId}
	,TITLE                  = #{title}
	,CONTENTS               = #{contents}
	,DEADLINE               = #{deadline , jdbcType=DATE}
	,EVALUATIONPERIOD       = #{evaluationPeriod , jdbcType=DATE}
	,FLAG                   = #{flag , jdbcType=INTEGER}
	,POINT                  = #{point , jdbcType=INTEGER}
WHERE ID	= #{taskId}
