mapper.insert:

	SELECT TASK_TB_ID_SEQ.NEXTVAL
	FROM DUAL

	INSERT
	INTO TASK_TB
	(
		ID
		,TITLE
		,CONTENTS
		,DEADLINE
		,FLAG
		,EVALUATION_PERIOD_START
		,EVALUATION_PERIOD_END
		,POINT
		,EVALUATED 
		,EVALUATION_CNT
		,TEAM_CODE
	)
	VALUES
	(
		#{taskId}
		,#{title}
		,#{contents, jdbcType=VARCHAR}
		,#{deadline, jdbcType=VARCHAR}
		,#{flag, jdbcType=INTEGER}
		,#{EVALUATION_PERIOD_START, jdbcType=VARCHAR}
		,#{EVALUATION_PERIOD_END, jdbcType=VARCHAR}
		,#{point, jdbcType=INTEGER}
		,#{evaluated, jdbcType=INTEGER}
		,#{EVALUATION_CNT, jdbcType=INTEGER}
		,#{TEAM_CODE, jdbcType=INTEGER}
	)

mapper.update:
UPDATE TASK_MEMBER_TB
	SET 
	SUBMISSION_ID			=(SELECT ID FROM TASK_TB WHERE ID=(SELECT MAX(ID) FROM TASK_TB))
	,COMMITTED 				=1
	WHERE ASSIGNMENT_ID		= #{ASSIGNMENT_ID}
	AND MEMBER_ID			= #{loginedMEMBER_ID }

mapper.insert:

	SELECT TASK_TB_ID_SEQ.NEXTVAL
