	)
	VALUES
	(
		#{taskId}
		,#{title}
		,#{contents, jdbcType=VARCHAR}
		,#{flag, jdbcType=INTEGER}
		,#{point, jdbcType=INTEGER}
		,#{evaluated, jdbcType=INTEGER}
		,#{EVALUATION_CNT, jdbcType=INTEGER }
		,#{TEAM_CODE, jdbcType=INTEGER}
	)

mapper.update:
UPDATE TASK_TB		
	SET
	ID =						#{taskId, jdbcType= INTEGER}
	,TITLE =					#{title, jdbcType= VARCHAR}
	,CONTENTS =					#{contents, jdbcType= VARCHAR}
	,DEADLINE =					#{deadline , jdbcType= VARCHAR}
	,EVALUATION_PERIOD_START =	#{EVALUATION_PERIOD_START ,jdbcType= VARCHAR}
	,EVALUATION_PERIOD_END =		#{EVALUATION_PERIOD_END ,jdbcType= VARCHAR}
WHERE ID = #{taskId}

UPDATE TASK_TB
	SET POINT				= #{point, jdbcType=INTEGER}
		,EVALUATION_CNT		= #{EVALUATION_CNT, jdbcType=INTEGER}
		,EVALUATED			= #{evaluated, jdbcType=INTEGER }
WHERE ID					= #{taskId}
