mapper.select:
		SELECT CODE, CYCLE, ENDDATE, LEADERID, NAME
		FROM TEAM_TB
		WHERE CODE = #{teamCode}

mapper.delete:
		DELETE FROM BELONG_TB
		WHERE MEMBERID = #{memberId} AND
		TEAMCODE = #{teamCode}

mapper.update:
UPDATE TEAM_TB 
SET NAME = #{name}, CYCLE = #{cycle}, ENDDATE = #{endDate} 
WHERE CODE = #{code}
